import { useState } from 'react'
import { BackBtn, ForwardBtn, UserBtn } from './form'
import { userService } from "../services/user.service.local"
import { LoginSignup } from './login-signup.jsx'
import { logout } from '../store/user.actions'
import { loadUser } from '../store/user.actions';

import { useSelector } from 'react-redux'

export function AppHeader() {
  const [user1, setUser1] = useState(userService.getLoggedinUser())
  const playlist = useSelector(storeState => storeState.playlistModule.playlist)
  const user = useSelector(storeState => storeState.userModule.user)




  function onChangeLoginStatus(user) {
    setUser1(user1)
  }
  function onLogout() {
    logout()
      .then(() => {
        setUser1(null)
      })
  }
  return <>
    <header className="app-header">
      <main>
        <nav>
          <BackBtn />
          <ForwardBtn />
        </nav>
        <UserBtn />
        {user1 ? (
          < section >
            <span>Hello {user1.username} </span>
            <button className="user-login-logout" onClick={onLogout}> Logout</button>
          </ section >
        ) : (
          <section>
            <LoginSignup onChangeLoginStatus={onChangeLoginStatus} />
          </section>
        )}

      </main>
    </header>
  </>
}
