import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { SET_VOLUME } from '../store/player.reducer';
import { DiviceBtn, MikeBtn, QueueBtn, VolumeBtn } from './form';

export function OptionsBar() {
    const volume = useSelector((storeState) => storeState.playerModule.volume);
    const dispatch = useDispatch();

    const handleChange = (event) => {
        dispatch({ type: SET_VOLUME, volume: parseFloat(event.target.value) });
    };

    return (
        <section className="options-bar">
            <MikeBtn />
            <QueueBtn />
            <DiviceBtn />
            <div className="volume-bar">
                <VolumeBtn />
                <div className="slider-container">
                    <input
                        type="range"
                        min="0"
                        max="1"
                        step="0.1"
                        className="slider"
                        value={volume}
                        onChange={handleChange}
                    />
                    <div className="progress" style={{ width: `${volume * 100}%` }}></div>
                </div>
            </div>
        </section>
    );
}
