import { useState } from 'react'
import { BackBtn, ForwardBtn, UserBtn } from './form'
import { userService } from "../services/user.service.local"
import { LoginSignup } from './login-signup.jsx'
import { logout } from "../store/user.actions"
import { useDispatch, useSelector } from 'react-redux'
import { SET_USER } from '../store/user.reducer'

export function AppHeader() {
  const [user1, setUser1] = useState(userService.getLoggedinUser())
  const user = useSelector(storeState => storeState.userModule.user)
  const dispatch = useDispatch()

  // function setUser(user) {
  //   dispatch({ type: SET_USER, user })
  // }
  // function onChangeLoginStatus(user1) {
  //   setUser1(user1)
  // }
  function onLogout() {
    logout()
    // .then(() => {
    //   setUser1(null)
    // })
  }
  return <>
    <header className="app-header">
      <main>
        <nav>
          <BackBtn />
          <ForwardBtn />
        </nav>
        <UserBtn />
        {user ? (
          < section >
            <span>Hello {user.username} </span>
            <button className="user-login-logout" onClick={onLogout}> Logout</button>
          </ section >
        ) : (
          <section>
            <LoginSignup />
          </section>
        )}

      </main>
    </header>
  </>
}
